package com.itssvkv.darkpix.data.local.repositoryimport android.content.Contextimport com.itssvkv.darkpix.utils.sharedPref.SharedPrefimport javax.inject.Injectclass SharedPrefRepositoryImp @Inject constructor() : SharedPrefRepository {    override fun saveToPref(context: Context, key: String, value: Any) =        SharedPref.saveToPref(context, key, value)    override fun getFromPref(context: Context, key: String, defValue: Any): Any =        SharedPref.getFromPref(context, key, defValue)}