package com.itssvkv.darkpix.data.networkimport com.itssvkv.darkpix.models.responses.AccessTokenimport retrofit2.http.Fieldimport retrofit2.http.POSTimport retrofit2.http.FormUrlEncodedinterface ApiServers {    @POST("oauth/token")    @FormUrlEncoded    suspend fun getAccessToken(        @Field ("client_id") clientId : String,        @Field ("client_secret") clientSecret : String,        @Field ("redirect_uri") redirectUri : String,        @Field ("code") code : String,        @Field ("grant_type") grantType : String    ): AccessToken}