package com.itssvkv.darkpix.diimport com.itssvkv.darkpix.data.local.repository.ApiCallsRepositoryimport com.itssvkv.darkpix.data.local.repository.ApiCallsRepositoryImpimport com.itssvkv.darkpix.data.local.repository.SharedPrefRepositoryimport com.itssvkv.darkpix.data.local.repository.SharedPrefRepositoryImpimport dagger.Moduleimport dagger.Providesimport dagger.hilt.InstallInimport dagger.hilt.components.SingletonComponentimport javax.inject.Singleton@Module@InstallIn(SingletonComponent::class)object RepositoryModule {    @Provides    @Singleton    fun providesApiCallsRepository(apiCallsRepositoryImp: ApiCallsRepositoryImp): ApiCallsRepository {        return apiCallsRepositoryImp    }    @Provides    @Singleton    fun providesSharedPrefRepository(sharedPrefRepositoryImp: SharedPrefRepositoryImp): SharedPrefRepository {        return sharedPrefRepositoryImp    }}