package com.itssvkv.darkpix.ui.bottomnavimport androidx.fragment.app.Fragmentimport androidx.viewpager2.adapter.FragmentStateAdapterimport com.itssvkv.darkpix.ui.home.HomeFragmentimport com.itssvkv.darkpix.ui.search.SearchFragmentclass TabsAdapter (fragment: TabsFragment) : FragmentStateAdapter(fragment) {    override fun getItemCount() = 2    override fun createFragment(position: Int): Fragment {        when(position){            0 -> return HomeFragment()            1 -> return SearchFragment()        }        return HomeFragment()    }}