package com.itssvkv.darkpix.ui.homeimport androidx.lifecycle.LiveDataimport androidx.lifecycle.MutableLiveDataimport androidx.lifecycle.ViewModelimport androidx.lifecycle.viewModelScopeimport com.itssvkv.darkpix.models.adaptersdata.CategoryDataimport com.itssvkv.darkpix.data.local.repository.ApiCallsRepositoryimport dagger.hilt.android.lifecycle.HiltViewModelimport kotlinx.coroutines.launchimport javax.inject.Inject@HiltViewModelclass HomeViewModel @Inject constructor(private val repo: ApiCallsRepository) : ViewModel() {    private val _categoryLiveData = MutableLiveData<List<CategoryData>>()    val categoryLiveData: LiveData<List<CategoryData>>        get() = _categoryLiveData    fun getCategoryData() = viewModelScope.launch {        _categoryLiveData.postValue(repo.getCategoryData())    }    suspend fun getAllPhotos() =        repo.getAllPhotos().flow}